class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dp(n, 0);
        for (int i = 1; i < n; i++) {
            dp[i] = dp[i - 1] + 1;
        }

        set<int> st;
        for (int i = 0; i < n; ++i) {
            st.insert(i);
        }

        vector<int> ans;
        for (const auto &it : queries) {
            int l = it[0];
            int r = it[1];

            auto lb = st.lower_bound(l + 1);
            auto ub = st.lower_bound(r);

            st.erase(lb, ub);

            ans.push_back(st.size() - 1);
        }

        return ans;
    }
};
