#include <vector>
#include <algorithm>
class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
           vector<int> answer(n, 0);
        for (int i = 1; i < n; ++i) {
            answer[i] = answer[i - 1] + 1;
        }

        set<int> st;
        for (int i = 0; i < n; ++i) {
            st.insert(i);
        }

        vector<int> r;

        for (const auto& qi : q) {
            int l = qi[0];
            int rt = qi[1];

            auto lb = st.lower_bound(l + 1);
            auto ub = st.lower_bound(rt);

            st.erase(lb, ub);

            r.push_back(st.size() - 1);
        }

        return r;
    }
};
