class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
                vector<int> dp(n, 0);
        for (int i = 1; i < n; ++i) {
            dp[i] = dp[i - 1] + 1;
        }

        set<int> myset;
        for (int i = 0; i < n; ++i) {
            myset.insert(i);
        }

        vector<int> result;

        for (const auto& qi : queries) {
            int left = qi[0];
            int right = qi[1];

            auto lb = myset.lower_bound(left + 1);
            auto ub = myset.lower_bound(right);

            myset.erase(lb, ub);

            result.push_back(myset.size() - 1);
        }

        return result;
    }
};
