class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeSet<Integer> tset = new TreeSet<>();
        for (int i = 0; i < n; ++i) {
            tset.add(i);
        }

        List<Integer> al = new ArrayList<>();

        for (int[] qe : queries) {
            int a = qe[0];
            int b = qe[1];

            SortedSet<Integer> set = tset.subSet(a + 1, b);
            List<Integer> rm = new ArrayList<>(set);
            tset.removeAll(rm);

            al.add(tset.size() - 1);
        }

        int[] res = new int[al.size()];
        int i = 0;
        for(int e : al) {
            res[i++] = e;
        }

        return res;
    }
}
