class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
         vector<int>dp(n,0);
        int i=1;
        while(i<n){
            dp[i]=dp[i-1]+1;
            ++i;
        }
        set<int>ans;
        int j=0;
        while(j<n){
            ans.insert(j);
            ++j;
        }
        vector<int>p;

        for(const auto &v: queries){
            int l=v[0];
            int r=v[1];
            auto lb=ans.lower_bound(l+1);
            auto ub=ans.lower_bound(r);
            ans.erase(lb,ub);
            p.push_back(ans.size()-1);




        }
        return p;


    }
};
