class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i=0; i<n; i++) st.insert(i);

        vector<int> ans;
        for(auto &q: queries){
            int x=q[0], y=q[1];
            st.erase(st.upper_bound(x), st.lower_bound(y));
            ans.push_back(st.size()-1);
        }

        return ans;
    }
};
