class Solution {
public:
    int fa[200005];
    int find(int x){
        while(fa[fa[x]]!=fa[x]){
            fa[x]=fa[fa[x]];
        }
        return fa[x];
    }
    vector<int> shortestDistanceAfterQueries(int n,
                                             vector<vector<int>>& queries) {
        for (int i = 1; i <= n; i++) {
            fa[i] = i;
        }
        vector<int> res;
        int ans = n - 1;
        for (int i = 0; i < queries.size(); i++) {
            int a = queries[i][0], b = queries[i][1];
            a++;b++;
            a=find(a);b=find(b);
            if(a==b){
                res.push_back(ans);
                continue;
            }
            a=find(a+1);
            while(true){
                if(a==b)break;
                int f=find(a+1);
                fa[a]=b;ans--;
                a=f;
            }
            res.push_back(ans);
        }
        return res;
    }
};
