class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        visited_u, visited_v = {},{}
        output = []
        shortest_path = n-1
        nodes = set()
        for i in range(n):
            nodes.add(i)
        for query in queries:
            if query[0] not in nodes or query[1] not in nodes:
                output.append(shortest_path)
                continue
            elif query[0] in visited_u:
                count_removed = 0
                for node in range(visited_u[query[0]]+1, query[1]):
                    if node not in nodes:
                        count_removed +=1
                shortest_path -= query[1] - visited_u[query[0]] - count_removed
                for node in range(visited_u[query[0]], query[1]):
                    if node in nodes:
                        nodes.remove(node)
                del visited_v[visited_u[query[0]]]
                visited_u[query[0]] = query[1]
                visited_v[query[1]] = query[0]
            elif query[1] in visited_v:
                count_removed = 0
                for node in range(query[0]+1, visited_v[query[1]]):
                    if node not in nodes:
                        count_removed +=1
                shortest_path -= visited_v[query[1]] - query[0]  - count_removed
                for node in range(query[0]+1, visited_v[query[1]]+1):
                    if node in nodes:
                        nodes.remove(node)
                del visited_u[visited_v[query[1]]]
                visited_u[query[0]] = query[1]
                visited_v[query[1]] = query[0]
            else:
                count_removed = 0
                for node in range(query[0]+1, query[1]):
                    if node not in nodes:
                        count_removed +=1
                shortest_path -= query[1]-query[0] -1  - count_removed
                for node in range(query[0]+1, query[1]):
                    if node in nodes:
                        nodes.remove(node)
                visited_u[query[0]] = query[1]
                visited_v[query[1]] = query[0]
            output.append(shortest_path)
        return output
