class Solution {
public:
    int fa[100005];
    int Find(int u) {
        if (fa[u] == u) return u;
        return fa[u] = Find(fa[u]);
    }
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> answer;
        for (int i = 0; i < n; i++) {
            fa[i] = i;
        }
        int ans = n - 1;
        for(auto vec : queries) {
            int l = vec[0], r = vec[1] - 1;
            if (r > l) {
                while (Find(l) != Find(r)) {
                    int fl = Find(l), fl1 = Find(fl + 1);
                    fa[fl] = fl1;
                    l = fl1;
                    ans--;
                }
            }
            answer.push_back(ans);
        }
        return answer;
    }
};
