class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        from sortedcontainers import SortedList
        sd = SortedList(range(n))


        res = []
        for f, t in queries:
            if f in sd and t in sd:
                temp = []
                for idx in range(sd.bisect_right(f), sd.bisect_left(t)):
                    temp.append(sd[idx])
                for tt in temp:
                    sd.remove(tt)

            res.append(len(sd) - 1)

        return res
