class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> next(n);
        for (int i = 0; i < n - 1; i++) {
            next[i] = i + 1;
        }

        vector<int> is_present(n, 1), ans;
        int cur = n - 1;

        for (auto &uv : queries) {
            int u = uv[0], v = uv[1];
            if (is_present[u] == 0) {
                ans.push_back(cur);
                continue;
            }

            int len = 0;
            for (int i = u; i < v; i = next[i]) {
                len++;
                if (i != u) is_present[i] = 0;
            }
            cur = cur - len + 1;
            next[u] = v;

            ans.push_back(cur);
        }

        return ans;
    }

    /*
    0 -> 1 -> 2 -> 3 -> 4
    */
};
