from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        graph = SortedList([i for i in range(n)])
        ans = []
        for xi, yi in queries:
            index1 = graph.bisect_left(xi)
            index2 = graph.bisect_left(yi)
            if graph[index1] == xi and graph[index2] == yi:
                for i in range(index1 + 1, index2):
                    graph.remove(graph[index1 + 1])
            ans.append(len(graph) - 1)
        return ans
