class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>se;
        for(int i=0;i<n;++i) se.insert(i);
        vector<int> ans;
        for(auto q:queries){
            int u=q[0],v=q[1];
            auto it=se.lower_bound(u+1);
            while(it!=se.end()&&(*it)<v){
                int val=(*it);
                it++;
                se.erase(val);
            }
            ans.push_back(se.size()-1);
        }
        return ans;
    }
};
