const int N = 1000007;

int par[N];

int get(int x) {
    return x == par[x] ? x : (par[x] = get(par[x]));
}

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        for (int i = 0; i < n; ++i) {
            par[i] = i;
        }

        int len = n - 1;
        vector<int> ans;
        for (auto& q : queries) {
            int from = q[0], to = q[1];
            int cur = from + 1;
            while (cur < to) {
                cur = get(cur);
                if (cur < to) {
                    int next = get(cur + 1);
                    --len;
                    par[cur] = next;
                    cur = next;
                }
            }
            ans.push_back(len);
        }
        return ans;
    }
};
