class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans, nxt(n, -1);
		for (int i = 0; i < n; ++i) nxt[i] = i + 1;
		// cout << "nxt: " << endl;
		int now = n-1;
		vector<int> del(n, 0);
		for (auto &qq : queries) {
			int x = qq[0], y = qq[1];
			// cout << "x: " << x << " y: " << y << endl;
			while (nxt[x] != y) {
				// cout << x << " " << nxt[x] << " " << y << endl;
				if (del[x]) break;
				if (nxt[x] == -1) break;
				if (nxt[x] > y) break;
				del[nxt[x]] = 1;
				nxt[x] = nxt[nxt[x]];
				now -= 1;
			}
			ans.push_back(now);
		}
		return ans;
    }
};
