class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {

        set<int> s;

        for(int i=0;i<n;i++){
            s.insert(i);
        }
        int ans=n-1;
        vector<int> v;

        for(int i=0;i<q.size();i++){
           int x=q[i][0];
           int y=q[i][1];
           vector<int> temp;

        auto p=s.upper_bound(x);
        int cnt=0;
        while(*p<y){
            temp.push_back(*p);
            p++;
        }
        for(int j=0;j<temp.size();j++){
            if(temp[j]>x && temp[j]<y){
                cnt++;
            s.erase(temp[j]);
            }
        }
        ans-=cnt;
        v.push_back(ans);

        }
        return v;


    }
};
