class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int m = q.size();
        vector<int> ans(m);

        multiset<int> ms;

        for(int i = 0 ;i < n;i++){
            ms.insert(i);
        }


        for(int i = 0 ;i < m;i++){
            int u = q[i][0];
            int v = q[i][1];

            int x = *ms.upper_bound(u);

            while(x < v){
                ms.erase(x);
                x = *ms.upper_bound(u);
            }

            // for(int j = y ; j >= x ;j--){
            //     if(ms.find(j) != ms.end()){
            //         ms.erase(j);
            //     }
            // }


            ans[i] = ms.size() - 1;
        }
        return ans;
    }
};
