class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set<int> s;
        for(int i=0;i<n;i++){
            s.insert(i);
        }
        for(int i=0;i<queries.size();i++){
            int u=queries[i][0],v=queries[i][1];
            auto it=s.lower_bound(u+1);
            while(it!=s.end()&&*it<v){
                it=s.erase(it);
            }
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
