class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        int sum=n-1;
        set<pair<int,int>> s;
        for(auto it:queries){
            int x=it[0],y=it[1];
            pair<int,int> p=make_pair(x,y);
            auto it2=s.lower_bound(make_pair(p.first+1,p.second));
            if(it2!=s.begin()&&prev(it2)->first<=p.first&&prev(it2)->second>=p.second){

            //it2=prev(it2);
            }
            else{
                while(s.size()){
                    auto it2=s.lower_bound(make_pair(p.first-1,p.second));
                    //if(it2!=s.end())printf("%d %d\n",it2->first,it2->second);
                    if(it2!=s.end()&&it2->first<y){
                        sum+=it2->second-it2->first-1;
                        s.erase(it2);
                    }
                    else{
                        break;
                    }
                }
                sum-=y-x-1;
                s.insert(p);
            }
            ans.push_back(sum);
        }
        return ans;
    }
};
