class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& a) {
        vector<int> res;
        map<int, int> m;
        for(int i = 0; i < n - 1; i++) {
            m[i] = i + 1;
        }
        for(auto &i : a) {
            int x = i[0], y = i[1];
            if(m.count(x)) {
                if(m[x] < y) {
                    auto it = m.lower_bound(x);
                    for(it++; it != m.end();) {
                        if(it != m.end() && it->second <= y) {
                            it = m.erase(it);
                        } else {
                            break;
                        }
                    }
                    m[x] = y;
                }
            }
            res.push_back(m.size());
        }
        return res;
    }
};
