class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int dist = n - 1, q = queries.length;
        TreeSet<Integer> visiting = new TreeSet<>();
        for (int i = 0; i < n; i++)
            visiting.add(i);

        int[] ans = new int[q];
        for (int i = 0; i < q; i++) {
            int u = queries[i][0], v = queries[i][1];
            for (int x = visiting.higher(u); x < v; x = visiting.higher(x))
                visiting.remove(x);

            ans[i] = visiting.size() - 1;
        }

        return ans;
    }
}
