class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        set<int> setCity;
        for (int i = 0; i < n; i++)
            setCity.insert(i);

        vector<int> result;
        for (vector<int>& query: queries) {

            auto start = setCity.upper_bound(query[0]);
            auto end = setCity.lower_bound(query[1]);
            setCity.erase(start, end);

            /*for (const int i: setCity)
                cout<<i <<" ";
            cout<<endl;*/

            result.push_back(setCity.size()-1);
        }

        return result;
    }
};
