from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        sl = SortedList(range(n))
        ans = []
        for i, j in queries:
            index = sl.bisect_left(i)
            if sl[index] == i:
                index += 1
                while index < len(sl) and sl[index] < j:
                    sl.pop(index)
            # print(sl)
            ans.append(len(sl) - 1)

        return ans
