class Solution {
public:
    using pii = pair<int, int>;

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> res;

        set<pii> st;
        int L = n-1;

        for (auto i : queries) {
            // cout << "----\n";

            int x = i[0], y = i[1]; // x -> y
            auto it = st.lower_bound(pii(x, 0));

            if (it != st.begin() && prev(it)->second >= y || it->first <= x && it->second >= y) {
                res.push_back(L);
                continue;
            }

            while (it != st.end() && it->second <= y) {
                // cout << L << " " << it->second - it->first - 1 << "\n";

                L += it->second - it->first - 1;
                it = st.erase(it);
            }

            st.emplace(x, y);
            L -= y - x - 1;

            res.push_back(L);
        }
        return res;
    }
};
