class Solution {
   public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeSet<Integer> set = new TreeSet<>();
        for (int i = 0; i < n; i++) {
            set.add(i);
        }
        int[] ans = new int[queries.length];
        int i = 0;
        for (int[] q : queries) {
            for (int v = set.ceiling(q[0] + 1); v < q[1]; v = set.ceiling(v + 1)) {
                set.remove(v);
            }
            ans[i++] = set.size() - 1;
        }
        return ans;
    }
}
