class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i = 0; i < n; i++){
            s.insert(i);
        }
        vector<int> ans(0);
        for(int i = 0; i < queries.size(); i++){
            int l = queries[i][0];
            int r = queries[i][1];
            while(true){
                auto ptr = s.upper_bound(l);
                int val = *ptr;
                if(val >= r){
                    break;
                }
                s.erase(val);
            }
            ans.push_back(s.size() - 1);
        }
        return ans;
    }
};
