class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qs) {
        set<int> st;
        for(int i = 0; i < n; i ++){
            st.insert(i);
        }
        vector<int> ans;
        for(auto &q : qs){
            int x = q[0];
            int y = q[1];
            auto iter = st.lower_bound(x+1);
            if(iter == st.end()){
                ans.push_back(st.size()-1);
                continue;
            }
            while(iter != st.end() && *iter < q[1]){
                auto x = iter;
                iter++;
                st.erase(x);
            }
            ans.push_back(st.size()-1);
        }
        return ans;

    }
};
