from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        sl=SortedList([i for i in range(n)])
        def find(target):
            idx=sl.bisect_left(target)
            if idx>=len(sl):
                return None
            if sl[idx]!=target:
                return None
            return idx
        res=[]
        for s,d in queries:
            sidx=find(s)
            if sidx==None:
                res.append(len(sl)-1)
                continue
            didx=find(d)
            if didx==None:
                res.append(len(sl)-1)
                continue
            rmary=[]
            for idx in range(sidx+1,didx):
                rmary.append(sl[idx])
            for ele in rmary:
                sl.remove(ele)
            res.append(len(sl)-1)
        return res
