#include <bits/stdc++.h>
#define sz size()
#define bk back()
#define fi first
#define se second

using namespace std;
typedef long long ll;
typedef pair<int, int> pii;

class Solution {
  public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>> &queries) {
        set<int> s;
        for (int i = 0; i < n; i++)
            s.insert(i);

        int q = queries.sz;
        vector<int> ret(q);

        for (int i = 0; i < q; i++) {
            int x = queries[i][0];
            int y = queries[i][1];

            auto it = s.lower_bound(x);
            if (*it == x) {
                vector<set<int>::iterator> v;

                while (1) {
                    it++;
                    if (*it >= y)
                        break;

                    v.push_back(it);
                }

                while (v.sz) {
                    s.erase(v.bk);
                    v.pop_back();
                }
            }

            ret[i] = s.sz - 1;
        }

        return ret;
    }
};
