class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = [i for i in range(n)]
        rank = [0]*n
        r = [i+1 for i in range(n)]
        def find(x):
            if g[x] != g[g[x]]:
                g[x] = find(g[x])
            return g[x]
        def union(x, y):
            X, Y = find(x), find(y)
            if X != Y:
                if rank[X] < rank[Y]:
                    g[X] = Y
                elif rank[X] == rank[Y]:
                    g[X] = Y
                    rank[Y] += 1
                else:
                    g[Y] = X
        m = len(queries)
        res = []
        cur = n-1
        for u, v in queries:
            if find(u) == find(v-1):
                res.append(cur)
                continue
            nxt = r[u]
            while nxt < v:
                union(u, nxt)
                cur -= 1
                nxt = r[nxt]
            r[u] = v
            res.append(cur)
        return res
