class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        std::set<int> st;
        for (int i = 0; i < n; i++) {
            st.emplace(i);
        }
        std::vector<int> ans;
        for (auto v : queries) {
            int uu = v[0], vv = v[1];
            auto it = st.upper_bound(uu);
            while (it != st.end() and (*it) < vv) {
                it = st.erase(it);
            }
            ans.emplace_back(st.size() - 1);
        }
        return ans;
    }
};
