class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i < n; ++i) {
            s.insert(i);
        }
        vector<int> ans;
        for (int i = 0; i < queries.size(); ++i) {
            int v = queries[i][0];
            int to = queries[i][1];

            auto it = s.find(v);
            vector<int> tr;
            if (it != s.end()) {
                while (it != s.end() && *it < to) {
                    if (*it != v) {
                        tr.push_back(*it);
                    }
                    ++it;
                }

                for (int j = 0; j < tr.size(); ++j) {
                    s.erase(tr[j]);
                }
            }

            ans.push_back((int)s.size() - 1);
        }

        return ans;
    }
};
