class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> m;
        int ans = n - 1;
        vector<int> ansv;
        for (auto &q : queries) {
            int l = q[0], r = q[1];
            if (m.count(l) && r <= m[l]) {
                ansv.push_back(ans);
                continue;
            }
            auto it = m.lower_bound(l);
            while (it != m.end() && it->second <= r) {
                ans += it->second - it->first - 1;
                it = m.erase(it);
            }
            if (it != m.begin()) {
                --it;
                if (it->second >= r) {
                    ansv.push_back(ans);
                    continue;
                }
            }
            ans -= r - l - 1;
            m[l] = r;
            ansv.push_back(ans);
        }
        return ansv;
    }
};
