class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i = 0;i<n;i++) st.insert(i);

        vector<int> ans;
        for(auto &it: queries){
            int l = it[0], r = it[1];
            auto itr = st.find(l);
            vector<int> rem;
            for(;itr != st.end();itr++){
                if(*itr == l) continue;
                if(*itr >= r) break;
                rem.push_back(*itr);
            }
            for(auto &x:rem) st.erase(x);
            ans.push_back(st.size() - 1);
        }
        return ans;
    }
};
