class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int q = queries.size();
        vector<int> ans(q);
        set<int> nodes;
        for (int i = 0; i < n; i++) {
            nodes.insert(i);
        }
        for (int i = 0; i < q; i++) {
            int l = queries[i][0], r = queries[i][1];
            set<int>::iterator it;
            while ((it = nodes.upper_bound(l)) != nodes.end() && *it < r) {
                nodes.erase(it);
            }
            ans[i] = nodes.size() - 1;
        }
        return ans;
    }
};
