from sortedcontainers import SortedSet
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        answer = []
        path = SortedSet([i for i in range(n)])
        for i, j in queries:
            k = path.bisect_left(i + 1)
            while path[k] <= j - 1:
                del path[k]
            answer += [len(path) - 1]
            #print(path)
        return answer
