class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        multiset<int> mst;
        for(int i = 0; i < n; i++){
            mst.insert(i);
        }
        int q = queries.size();
        vector<int> ans(q);
        for(int i = 0; i < q; i++){
            int l = queries[i][0];
            int r = queries[i][1];
            auto it = mst.upper_bound(l);
            vector<int> del;
            while(it != mst.end() && *it < r){
                del.push_back(*it);
                it = mst.upper_bound(*it);
            }
            for(auto d: del){
                mst.erase(d);
            }
            ans[i] = int(mst.size()) - 1;
        }
        return ans;
    }
};
