class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int, int>> H;
        for (int i = 1; i < n; ++i)
            H.insert(pair(i - 1, i));

        vector<int> res;
        for (auto& e : queries)
        {
            int x = e[0], y = e[1];

            while (1)
            {
                auto it = H.lower_bound(pair(x, x));
                if (it == H.end()) break;
                if (it->first >= y) break;
                H.erase(it);
            }

            while (1)
            {
                auto it = H.lower_bound(pair(x, x));
                if (it == H.begin()) break;
                it = prev(it);
                if (it->second <= x) break;
                H.erase(it);
            }

            H.insert(pair(x, y));
            res.push_back(H.size());
        }
        return res;
    }
};
