class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i=0; i<n; i++) st.insert(i);
        vector<int> res;
        auto xoa=[&](int u, int v) {
            auto start = st.lower_bound(u+1);
            auto end = st.upper_bound(v-1);
            st.erase(start, end);
        };
        for(auto&x:queries) {
            int u = x[0];
            int v = x[1];
            xoa(u, v);
            res.push_back(st.size()-1);
        }
        return res;
    }
};
