class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int> set;
        for (int i = 0; i < n; i++) {
            set.insert(i);
        }
        vector<int> ans;
        for (int i = 0; i < q.size(); i++) {
            int st = q[i][0], ed = q[i][1];
            auto it = set.lower_bound(st);
            if (it != set.end()) {
                it++;
                while (it != set.end() && *it < ed) {
                    set.erase(it++);
                }
            }            
            ans.emplace_back(set.size() - 1);
        }
        return ans;
    }
};
