class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
          set<int> st;
          for(int i=0;i<n;i++){

              st.insert(i);
          }

         vector<int> ans;

         int m=queries.size();
         for(int i=0;i<m;i++){

             int l=queries[i][0],r=queries[i][1];

             auto val=st.upper_bound(l);

             while(val!=st.end()&&*val<r){
                 st.erase(*val);
                 val=st.upper_bound(l);

             }

             ans.push_back(st.size()-1);
         }

        return ans;
    }
};
