class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int len = queries.length;
        int[] ans = new int[len];
        TreeSet<Integer> ts = new TreeSet<>();
        for (int i = 1; i < n - 1; i++)
            ts.add(i);
        for (int i = 0; i < len; i++) {
            int x = queries[i][0];
            int y = queries[i][1];
            while (true) {
                Integer higher = ts.higher(x);
                if (higher != null && higher < y) {
                    ts.remove(higher);
                    x = higher;
                } else {
                    break;
                }
            }
            ans[i] = ts.size() + 1;
        }
        return ans;
    }
}
