class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> sp;
        for(int i = 0; i < n - 1; ++i)
            sp.insert(i);
        vector<int> ans;
        for(auto &it: queries) {
            for(auto jt = sp.lower_bound(it[0]); jt != sp.end() && *jt < it[1] - 1; jt = sp.erase(jt));
            ans.push_back(sp.size());
        }
        return ans;
    }
};
