class Solution:
    def shortestDistanceAfterQueries(self, n, queries):
        dist = n - 1

        removed = set()
        skips = {}

        def addEdge(u, v):
            if u in removed:
                return

            i = u + 1
            while i < v:
                if i in removed:
                    i = skips[i]
                else:
                    removed.add(i)
                    i += 1

            skips[u + 1] = v

        ans = []
        for u, v in queries:
            addEdge(u, v)
            ans.append(dist - len(removed))

        return ans
