class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        t = list(range(n))
        answer = []
        for u, v in queries:
            begin = bisect.bisect_right(t, u)
            end = bisect.bisect_right(t, v)
            t[begin:end-1] = []

            answer.append(len(t)-1)

        return answer
