class Solution {
  public:
  vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
    set<int> st;
    for(int i = 0; i < n; i++) {
      st.insert(i);
    }
    vector<int> ans;
    for(auto &q : queries) {
      int x = q[0], y = q[1];
      while(true) {
        auto pos = st.upper_bound(x);
        if(*pos >= y) break;
        st.erase(pos);
      }
      int k = st.size();
      ans.push_back(k - 1);
    }
    return ans;
  }
};
