class Solution {
public:

    class ListNode
    {
        public:
            ListNode* next;
            int val;

            ListNode(int x)
            {
                next = nullptr;
                val = x;
            }

    };

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        vector <int> ans_vec;
        vector <bool> used(n,true);
        int ans = n-1;
        vector <ListNode*> nodes(n, nullptr);
        for (int i=0;i<n;i++)
        {
            nodes[i] = new ListNode(i);
        }
        for (int i=0;i<n-1;i++)
        {
            nodes[i]->next = nodes[i+1];
        }

        for (auto query : queries)
        {
            int from = query[0];
            int to = query[1];
            if (!used[from] || !used[to])
            {
                ans_vec.push_back(ans);
                continue;
            }
            auto it = nodes[from]->next;
            while (it != nullptr && it->val != to)
            {
                used[it->val] = false;
                ans--;
                it = it->next;
            }
            nodes[from]->next = nodes[to];
            ans_vec.push_back(ans);
        }

        return ans_vec;

