class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> res;
        set<pair<int,int>> f;
        int cur=n-1;
        for (int i=0;i<q.size();i++) {
            auto iter = f.lower_bound(make_pair(q[i][0],q[i][1]));
            if (iter!=f.begin()) iter--;
            while (iter != f.end()) {
                if (iter->second>q[i][1]) break;
                if (iter->second==q[i][1]&&iter->first<=q[i][0]) break;
                if (iter->second>q[i][0]) {
                    cur+=(iter->second - iter->first-1);
                    auto nex = iter; 
                    iter++;
                    f.erase(nex);
                } else iter++;
            }
            if (iter != f.end()&&iter->first<=q[i][0]&&iter->second>=q[i][1]) res.push_back(cur);
            else {
                cur-=(q[i][1]-q[i][0]-1);
                res.push_back(cur);
                f.insert(make_pair(q[i][0],q[i][1]));
            }
        }
        return res;
    }
};
