class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int> st;
        for(int i = 0 ; i < n ; i ++){
            st.insert(i);
        }
        vector<int> ans;
        for(int i = 0 ; i < (int) q.size() ; i ++){
            vector<int> to_remove;
            for(auto it = st.lower_bound(q[i][0] + 1) ; it != st.end() && *it < q[i][1] ; it ++){
                to_remove.push_back(*it);
            }
            for(auto &c : to_remove){
                // cout << c << " ";
                st.erase(c);
            }
            // cout << endl;
            // for(auto &c : st) cout << c << " " ; cout << endl;
            ans.push_back((int) st.size() - 1);
        }
        return ans;
    }
};
