class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        vector<int> ans;
        for(int i = 0; i<n; i++)s.insert(i);
        for(auto q: queries){
            int u = q[0]+1, v = q[1];
            while(1){
                auto it = s.lower_bound(u);
                if(it!=s.end() && *it<v)s.erase(it);
                else break;
            }
            int x = s.size() - 1;
            ans.push_back(x);
        }
        return ans;
    }
};
